import "bootstrap/dist/css/bootstrap.min.css"
import { useEffect, useState } from "react";

function App() {
{/* ë¦¬ì•¡íŠ¸ì—ì„œëŠ” ëª¨ë“  ë³€ìˆ˜ê°€ ë¶ˆë³€ì„±ì„ ì§€ë‹ˆê³  ìžˆë‹¤.
ë³€í•˜ê²Œ ë§Œë“¤ì–´ì£¼ë ¤ë©´ Hooksë¥¼ ì‚¬ìš©í•´ì•¼í•œë‹¤. í›…ìŠ¤ëŠ” ê°€ìž¥ ìƒë‹¨ì— ì¨ì¤€ë‹¤.
UseStateëŠ” constë¡œ ì„ ì–¸í•œë‹¤ */}
    const [count, setCount] = useState(0);
    // 1ë²ˆì§¸ ì¸ìž: ë³€ìˆ˜, 2ë²ˆì§¸ ì¸ìž: ë³€ìˆ˜ ê°’ì„ ë³€í™”ì‹œí‚¬ í•¨ìˆ˜
    // 2ë²ˆë•Œ ì¸ìžì˜ ê°€ìž¥ ì•žì—ëŠ” set í‚¤ì›Œë“œë¥¼ ë¶™ì—¬ì„œ ì‚¬ìš©!
    // useState(0) : 0ì€ ì´ˆê¸°ê°’ì„ ë‚˜íƒ€ë‚¸ë‹¤.
    const [count2, setCount2] = useState(1);


    // 1. useEffect ë°©ë²• : í•­ìƒ ë Œë”ë§ì´ ëœë‹¤. - rarely used
    // useEffect(() => {
    //     console.log("í•­ìƒ ë Œë”ë§ ðŸ‘Œ")
    // })

    // 2. useEffect ë°©ë²• : ì²˜ìŒì—ë§Œ ë Œë”ë§ì´ ëœë‹¤.
    useEffect(() => {
        console.log("ì²˜ìŒì—ë§Œ ë Œë”ë§ ðŸ‘Œ")
    }, [])

    // 3-1. useEffect ë°©ë²• : íŠ¹ì • ë³€ìˆ˜ (count) ì—ë§Œ ë Œë”ë§ì´ ëœë‹¤.
    useEffect(() => {
        console.log("countê°€ ë³€í•  ë•Œë§Œ ë Œë”ë§ ðŸ‘Œ")
    }, [count])

    // 3-2. useEffect ë°©ë²• : íŠ¹ì • ë³€ìˆ˜ (count2) ì—ë§Œ ë Œë”ë§ì´ ëœë‹¤.
    useEffect(() => {
        console.log("count2ê°€ ë³€í•  ë•Œë§Œ ë Œë”ë§ ðŸ‘Œ")
    }, [count2])


    function increase() {
        setCount(count + 10);
        // setCount((ì´ì „ê°’) => ì´ì „ê°’+1); // ê°€ëŠ¥..
    }

    function decrease() {
        setCount(count - 1);
        // setCount((prevValue) => prevValue-1); // ê°€ëŠ¥..
    }

    function multiple() {
        setCount(count2 * 2);
    }

    return (
        <div className = "container text-center mt-3">
            <h1>React Hooks</h1>
            <hr />
            <h4>Current Value is : {count}</h4>

            <button className = "btn btn-primary me-2"
                    onClick={increase}>
                Increase + 10
            </button>

            {/* 
            <button className = "btn btn-primary"
                    onClick={decrease}>
                Decrease
            </button> 
            */}

            <h4>Current Value is : {count}</h4>

            <button className = "btn btn-primary"
                    onClick={multiple}>
                Multiple * 2
            </button>

        </div>

    );
}

export default App;
